mapper.select:

SELECT a.item_code,a.item_name,a.item_no,a.item_specification,b.item_category
FROM ma_item a, ma_itemtype b
WHERE a.item_type = b.item_type AND NOT b.item_category = '원자재' AND company_code = #{company_code}


SELECT sitem_cd AS item_code, item_name, item_no, item_specification
FROM ma_bom_tree
WHERE ppitem_cd = #{ppitem_cd}
